/* eslint-disable jsx-a11y/no-static-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
import React, { useState } from 'react';
import './Carousel.css';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import { images } from './CarousselInfo';

function Carousel() {
  const [currentImage, setCurrentImage] = useState(0);

  return (
    <div className="carousel">
      <div
        className="carousel-inner"
        style={{ backgroundImage: `url(${images[currentImage].img})` }}
      >
        <div
          className="left"
          onClick={() => currentImage > 0 && setCurrentImage(currentImage - 1)}
        >
          <ArrowBackIosIcon style={{ fontSize: 50 }} />
        </div>
        <div className="center">
          <h1>{images[currentImage].title}</h1>
          <p className="subtitles"> {images[currentImage].subtitles} </p>
        </div>
        <div
          className="right"
          onClick={() =>
            currentImage < images.length - 1 &&
            setCurrentImage(currentImage + 1)
          }
        >
          <ArrowForwardIosIcon style={{ fontSize: 50 }} />
        </div>
      </div>
    </div>
  );
}

export default Carousel;
